import React from "react";
import { useSelector } from "react-redux";
// kêt nối react-reduct
const ModalGioHangRedux = () => {
  const gioHang = useSelector((state) => {
    return {
      gioHang: state.gioHang,
    };
  });
//   console.log(gioHang);

const listGioHang = () => {
    console.log(gioHang);
    const bindingGioHang = gioHang.map((item, index) => {
      return (
        <tr key={index}>
          <td>{item.maSP}</td>
          <td>{item.tenSP}</td>
          <td><img src={item.hinhAnh} alt={item.tenSP} width={25} height={75} /></td>
          <td>{item.hinhAnh}</td>
          <td>{item.soLuong}</td>
          <td>{item.giaBan}</td>
          <td>{item.thanhTien}</td>
        </tr>
      );
    });
    return <div>
    <table className="table">
        <thead>
            <tr>
                <th>Mã Sản Phẩm</th>
                <th>Tên Sản Phẩm</th>
                <th>Hình ảnh</th>
                <th>Số Lượng</th>
                <th>Giá Bán</th>
                <th>Thành Tiền</th>
            </tr>
        </thead>
        <tbody>
            {listGioHang}
        </tbody>
    </table>
  </div>;
  };



};

export default ModalGioHangRedux;
