import styled from "styled-components";
export const Button = styled.button `
    background-color: #f00;
    color: #fff;
    border: 1px solid #f00;
    padding: 10px 15px;
    border-radius: 4px;
    cursor: ${({disabled})=> (disabled?"not-allowed": "pointer")};
`;
