import React from "react";
import { useSelector } from "react-redux";
// kêt nối react-reduct
const ModalGioHangRedux = () => {
  const gioHang = useSelector((state) => {
    return {
      gioHang: state.gioHang,
    };
  });
  console.log(gioHang);

  const listGioHang = () => {
    gioHang.map((item, index) => {
      return (
        <tr key={index}>
          <td>{item.maSP}</td>
          <td><img src={item.tenSP} alt={item.tenSP} width={25} height={75} /></td>
          <td>{item.hinhAnh}</td>
          <td>{item.soLuong}</td>
          <td>{item.giaBan}</td>
        </tr>
      );
    });
  };

  return <div>
    <table className="table">
        <thead>
            <tr>
                <th>Mã Sản Phẩm</th>
                <th>Mã Sản Phẩm</th>
                <th>Mã Sản Phẩm</th>
                <th>Mã Sản Phẩm</th>
            </tr>
        </thead>

    </table>
  </div>;
};

export default ModalGioHangRedux;
