// Khởi tạo giá trị ban đầu của store
import * as actionTypes from "../redux/constants/gioHangConstants";

const stateGioHang = {
  gioHang: [
    {
      maSP: 1,
      tenSP: "Iphone",
      hinhAnh: "",
      soLuong: 1,
      giaBan: 1000,
      thanhTien: 1000,
    },
  ],
};

export const GioHangReducer = (state = stateGioHang, action) => {
  switch (action.type) {
    case actionTypes.ADD_GIOHANG:
      return [...state, action.data]

    default:
      return state;
      break;
  }

};
